    public List<ExcerptDTO> getExcerptsList() {

        String sql = "select e." + EXCERPT_BOOK_ID + ", e." + EXCERPT_ID + ", e." + EXCERPT_TITLE + ", e." + EXCERPT_TEXT + ", b." + BOOK_IMAGE
                + " from " + TABLE_NAME_BOOK + " b join " + TABLE_NAME_EXCERPT + " e on e.book_id = b.book_id";

        Cursor cursor = this.getReadableDatabase().rawQuery(sql, null);

        ArrayList<ExcerptDTO> excerptList = new ArrayList<>();
        try {
            if (cursor.getCount() > 0) {
                cursor.moveToFirst();

                while (!cursor.isAfterLast()) {
                    int bookId = Integer.parseInt(cursor.getString(0));
                    int excerptId = Integer.parseInt(cursor.getString(1));
                    String excerptTitle = cursor.getString(2);
                    String excerptText = cursor.getString(3);
                    byte[] imageBlob = cursor.getBlob(4);
                    ExcerptDTO dto = new ExcerptDTO(bookId, excerptId, excerptTitle, excerptText, imageBlob);
                    excerptList.add(dto);
                    cursor.moveToNext();
                }
            }
        } catch (Exception e) {
            throw e;
        } finally {
            this.close();
        }
        return excerptList;
    }
    public byte[] convertImageToByte(int drawable){
        ByteArrayOutputStream baos = new ByteArrayOutputStream();
        Bitmap bitmap = ((BitmapDrawable) ContextCompat.getDrawable(context, drawable)).getBitmap();
        bitmap.compress(Bitmap.CompressFormat.JPEG, 100, baos);
        return baos.toByteArray();
    }

    public void insertIntoBooksTable(SQLiteDatabase db,  String bookName, byte[] imageBlob, String flipkartLink, String amazonLink){
        ContentValues values = new ContentValues();
        values.put(BOOK_NAME, bookName);
        values.put(BOOK_IMAGE, imageBlob);
        values.put(BOOK_URL_FLIPKART, flipkartLink);
        values.put(BOOK_URL_AMAZON, amazonLink);
        db.insert(TABLE_NAME_BOOK, null, values);
    }

    public void insertIntoExcerptsTable(SQLiteDatabase db,  int bookId, String excerptTitle, String excerptText, int isExcerptLiked ){
        ContentValues values = new ContentValues();
        values.put(EXCERPT_BOOK_ID, bookId);
        values.put(EXCERPT_TITLE, excerptTitle);
        values.put(EXCERPT_TEXT, excerptText);
        //values.put(EXCERPT_LIKE, isExcerptLiked);
        db.insert(TABLE_NAME_EXCERPT, null, values);
    }
